//This module is used to create atoms which do not have a set number of inputs, but instead always have one input free.

/**
 * Computes the number of inputs which are curently available on a target atom.
 * @param {object} target - The atom which should have it's number of inputs computed.
 */ 
const howManyInputPortsAvailable = function(target){
    var portsAvailable = 0
    target.inputs.forEach(io => {
        if(io.type == 'input' && io.connectors.length == 0){   //if this port is available
            portsAvailable = portsAvailable + 1  //Add one to the count
        }
    })
    return portsAvailable
}

/**
 * Deletes one free input from an atom which has more than one free input available.
 * @param {object} target - The atom which should have one input deleted (if there are more than two free).
 */ 
const deleteEmptyPort = function(target){
    var i
    for (i = 0; i < target.inputs.length - 1; i++) {
        var io = target.inputs[i]
        if(io.type == 'input' && io.connectors.length == 0){
            target.removeIO('input', io.name, target)
            return
        }
    }
}

/**
 * Finds the highest number input currently used by this atom
 * @param {object} target - The atom which should be inspected for inputs.
 */ 
const findHighestInput = function(target){
    var maxInput = 0
    target.inputs.forEach( input => {
        maxInput = Math.max(maxInput, parseInt(input.name.match(/\d+$/)[0]))
    })
    return maxInput
}

/**
 * Adds or deletes inputs from a target atom until there is exactly one input available.
 * @param {object} target - The atom which should have it's number of inputs adjusted.
 */ 
export const addOrDeletePorts = (target) => {
    
    //Add or delete ports as needed
    if(howManyInputPortsAvailable(target) == 0){ //We need to make a new port available
        findHighestInput(target)
        target.addIO('input', '3D shape ' + (findHighestInput(target) + 1), target, 'geometry', '', true)
    }
    if(howManyInputPortsAvailable(target) >= 2){  //We need to remove the empty port
        deleteEmptyPort(target)
    }
}

import GlobalVariables from './globalvariables.js'

/**
 * This class defines a BOMEntry object which is used to define one entry in a bill of materials.
 */ 
export class BOMEntry {
    /**
     * The constructor returns a new blank BOMEntry object.
     */ 
    constructor(){
        /** 
         * The name of the item.
         * @type {string}
         */
        this.BOMitemName  = 'name'
        /** 
         * The number of this item needed.
         * @type {number}
         */
        this.numberNeeded = 1
        /** 
         * The cost of one of this item in USD.
         * @type {number}
         */
        this.costUSD      = 0
        /** 
         * A link to where to purchase the item.
         * @type {string}
         */
        this.source       = 'www.example.com'
        /** 
         * The total number of this item needed for this part. Should this be removed in favor of  forcing the parts to be modeled? Probably nobody would bother.
         * @type {number}
         */
        this.totalNeeded  = this.numberNeeded //Scaled by the number of this instance
    }
}

/**
 * Computes and returns an array of BOMEntry objects after looking at the tags of a geometry.
 * @param {object} geometry - The geometry which should be scanned for tags.
 */ 
export const extractBomTags = async(geometry) => {
    
    var bomItems = []
    
    var result = await GlobalVariables.ask({values: [geometry], key: "getBOM"})
    
    if (result != -1 ){
        //Filter for only bomItems
        bomItems = result.filter(item => {
            return item[0].substring(2, 13) == "BOMitemName"
        })
        
        bomItems = bomItems.map(JSON.parse)
        //bomItems = bomItems.map(JSON.parse)
        
        //Consolidate similar items into a single item
        var compiledArray = []
        bomItems.forEach(function (bomElement) {
            if (!this[bomElement.BOMitemName]) {
                this[bomElement.BOMitemName] = new BOMEntry
                this[bomElement.BOMitemName].BOMitemName = bomElement.BOMitemName
                this[bomElement.BOMitemName].source = bomElement.source
                compiledArray.push(this[bomElement.BOMitemName])
            }
            this[bomElement.BOMitemName].numberNeeded += bomElement.numberNeeded
            this[bomElement.BOMitemName].costUSD += bomElement.costUSD
        }, Object.create(null))
        
        //Alphabetize by source
        compiledArray = compiledArray.sort((a,b) => (a.source > b.source) ? 1 : ((b.source > a.source) ? -1 : 0)) 
        
        return compiledArray
    }
}

