import Atom from '../prototypes/atom.js'
import { addOrDeletePorts } from '../alwaysOneFreeInput.js'

/**
 * This class creates the shrinkwrap atom. This behavior can also be called 'hull'
 */
export default class ShrinkWrap extends Atom{
    
    /**
     * The constructor function.
     * @param {object} values An array of values passed in which will be assigned to the class as this.x
     */ 
    constructor(values){
        super(values)
        
        this.addIO('output', 'geometry', this, 'geometry', '')
        
        /**
         * This atom's name
         * @type {string}
         */
        this.name = 'Shrink Wrap'
        /**
         * This atom's type
         * @type {string}
         */
        this.atomType = 'ShrinkWrap'
        /**
         * A list of all of the inputs to this molecule. May be passed to the constructor and loaded.
         * @type {array}
         */
        this.ioValues = []
        /**
         * This was used when there was a drop down to select closed...may be deleted
         * @type {number}
         */
        this.closedSelection = 0
        /**
         * I believe this is no longer used alzatin - is that right?
         * @type {boolean}
         */
        this.addedIO = false
        
        this.setValues(values)
        
        if (typeof this.ioValues !== 'undefined'){
            this.ioValues.forEach(ioValue => { //for each saved value
                this.addIO('input', ioValue.name, this, 'geometry', '')
            })
        }
        
        this.updateValue()
    }
    
    /**
     * Generates a list of all of the input shapes, then passees them to a worker thread to compute the hull
     */ 
    updateValue(){
        if(this.inputs.every(x => x.ready)){
            try{
                var inputsList = []
                this.inputs.forEach( io => {
                    if(io.connectors.length > 0){
                        inputsList.push(io.getValue())
                    }
                })
                const values = inputsList.map(x => {
                    return x
                })
                
                this.basicThreadValueProcessing(values, "hull")
            }catch(err){this.setAlert(err)}
            
            //Delete or add ports as needed
            addOrDeletePorts(this)
        }
    }
     
    /**
     * Add the names of the inputs to the saved object so that they can be loaded later
     */ 
    serialize(savedObject){
        var thisAsObject = super.serialize(savedObject)
        
        var ioValues = []
        this.inputs.forEach(io => {
            if (io.type == 'input'){
                var saveIO = {
                    name: io.name,
                    ioValue: 10
                }
                ioValues.push(saveIO)
            }
        })
        
        ioValues.forEach(ioValue => {
            thisAsObject.ioValues.push(ioValue)
        })
        
        //Write the selection for if the chain is closed
        thisAsObject.closedSelection = this.closedSelection
        
        return thisAsObject
        
    }
    
}

import Atom from '../prototypes/atom'
import GlobalVariables from '../globalvariables.js'
import saveAs from '../lib/FileSaver.js'

/**
 * This class creates the stl atom which lets you download a .stl file.
 */
export default class Stl extends Atom {
    
    /**
     * The constructor function.
     * @param {object} values An array of values passed in which will be assigned to the class as this.x
     */ 
    constructor(values){
        
        super(values)
        
        /**
         * This atom's name
         * @type {string}
         */
        this.name = 'Stl'
        /**
         * This atom's type
         * @type {string}
         */
        this.atomType = 'Stl'
        
        /**
         * This atom's value. Contains the value of the input geometry, not the stl
         * @type {string}
         */
        this.value = null
        
        this.addIO('input', 'geometry', this, 'geometry', null)
        
        this.setValues(values)
    }
    
    /**
     * Set the value to be the input geometry, then call super updateValue()
     */ 
    updateValue(){
        if(this.inputs.every(x => x.ready)){
            try{
                this.value = this.findIOValue('geometry')
            }catch(err){this.setAlert(err)}
            super.updateValue()
        }
    }
    
    /**
     * Create a button to download the .stl file.
     */ 
    updateSidebar(){
        const list = super.updateSidebar()
        this.createButton(list, this, "Download STL", ()=>{this.downloadStl()})
    }
    
    /**
     * The function which is called when you press the download button.
     */ 
    downloadStl(){
        const values = [this.value]
        
        const computeValue = async (values, key) => {
            try{
                return await GlobalVariables.ask({values: values, key: key})
            }
            catch(err){
                this.setAlert(err)
            }
        }
        
        computeValue(values, "stl").then(result => {
            if (result != -1 ){
                const blob = new Blob([result], {type: 'text/plain;charset=utf-8'})
                saveAs(blob, GlobalVariables.topLevelMolecule.name+'.stl')
            }else{
                this.setAlert("Unable to compute")
            }
        })
    }
}

