document.getElementById('menuInput').addEventListener('keyup', (e) => {
    if(e.code == 'Enter'){
        searchMenu()}
})

/**  List that contains results of gitHub search */
let githubList = document.getElementById('githubList')

/**
 * Runs when a gitHub molecule menu option is clicked to show search bar.
 */ 
function showGitHubSearch(){
    //remove old results everytime           
    var oldResults = githubList.getElementsByClassName('menu-item')
    const menu = document.querySelector('#canvas_menu')

    for (let i = 0; i < oldResults.length; i++) {
        githubList.removeChild(oldResults[i])
        githubList.style.display = "none"
        menu.style.borderRadius = '50px'
    }

    const containerX = parseInt(cmenu._container.style.left, 10)
    const containerY = parseInt(cmenu._container.style.top, 10)
    menu.style.display = 'block'
    menu.style.top = `${containerY}px`
    menu.style.left = `${containerX}px`
    menu.classList.remove('off')

    const menuInput = document.getElementById('menuInput')
    menuInput.setAttribute('style','display:block')
    
}

/**
* Runs when enter key is clicked and the input is focused to show results from search.
*/ 
function searchMenu() {
    //We are searching on github
    let input = document.getElementById('menuInput').value

    var oldResults = githubList.getElementsByClassName('menu-item')
    for (let i = 0; i < oldResults.length; i++) {
        githubList.removeChild(oldResults[i])
    }
    GlobalVariables.gitHub.searchGithub(input).then(result => {
        result.data.items.forEach(item => {
            var newElement = document.createElement('LI')
            var text = document.createTextNode(item.name)
            const menu = document.querySelector('#canvas_menu')
            menu.style.borderRadius = '30px 30px 20px 20px'
            newElement.setAttribute('class', 'menu-item')
            newElement.setAttribute('id', item.id)
            newElement.appendChild(text) 
            githubList.appendChild(newElement) 
            githubList.setAttribute('style','display:block;')

 
            document.getElementById(item.id).addEventListener('click', (e) => {
                placeGitHubMolecule(e)
            })
        })
    })
    
}
/**
     * Runs when a menu option is clicked to place a new atom from the local atoms list.
     * @param {object} ev - The event triggered by click event on a menu item.
     */ 
function placeNewNode(e){
    let clr = e.target.id
    const containerX = parseInt(cmenu._container.style.left, 10)
    const containerY = parseInt(cmenu._container.style.top, 10)
    GlobalVariables.currentMolecule.placeAtom({
        x: GlobalVariables.pixelsToWidth(containerX), 
        y: GlobalVariables.pixelsToHeight(containerY), 
        parent: GlobalVariables.currentMolecule,
        atomType: clr,
        uniqueID: GlobalVariables.generateUniqueID()
            
    }, null, GlobalVariables.availableTypes, true) //null indicates that there is nothing to load from the molecule list for this one, true indicates the atom should spawn unlocked
}

/**
     * Runs when a menu option is clicked to place a new atom from searching on GitHub.
     * @param {object} ev - The event triggered by clicking on a menu item.
     */ 
function placeGitHubMolecule(ev){

    const menu = document.querySelector('#canvas_menu')
    menu.classList.add('off')
    menu.style.top = '-200%'
    menu.style.left = '-200%'  

    let clr = ev.target.id
    const containerX = parseInt(cmenu._container.style.left, 10)
    const containerY = parseInt(cmenu._container.style.top, 10)
    GlobalVariables.currentMolecule.placeAtom({
        x: containerX, 
        y: containerY, 
        parent: GlobalVariables.currentMolecule,
        atomType: 'GitHubMolecule',
        projectID: clr,
        uniqueID: GlobalVariables.generateUniqueID()
    }, null, GlobalVariables.availableTypes, true) //null indicates that there is nothing to load from the molecule list for this one
    
}

export {cmenu}

import AttachmentPoint from './attachmentpoint'
import GlobalVariables from '../globalvariables'
import showdown  from 'showdown'

/**
 * This class is the prototype for all atoms.
 */
export default class Atom {

    /**
     * The constructor function.
     * @param {object} values An array of values passed in which will be assigned to the class as this.x
     */ 
    constructor(values){
        //Setup default values
        /** 
         * An array of all of the input attachment points connected to this atom
         * @type {array}
         */
        this.inputs = []
        /** 
         * This atom's output attachment point if it has one
         * @type {object}
         */
        this.output = null
        
        /** 
         * The X cordinate of this atom
         * @type {number}
         */
        this.x = 0
        /** 
         * The Y cordinate of this atom
         * @type {number}
         */
        this.y = 0
        /** 
         * This atom's radius as displayed on the screen is 1/72 width
         * @type {number}
         */
        this.radius = 1/75
        /** 
         * This atom's default color (ie when not selected or processing)
         * @type {string}
         */
        this.defaultColor = '#F3EFEF'
        /** 
         * This atom's color when selected
         * @type {string}
         */
        /** 
         * The color to use for strokes when selected
         * @type {string}
         */
        this.selectedColor = '#484848'
        /** 
         * The color currently used for strokes
         * @type {string}
         */
        this.strokeColor = '#484848'
        /** 
         * A flag to indicate if this atom is curently selected
         * @type {boolean}
         */
        this.selected = false
        /** 
         * This atom's curent color
         * @type {string}
         */
        this.color = '#F3EFEF'
        /** 
         * This atom's name
         * @type {string}
         */
        this.name = 'name'
        /** 
         * This atom's parent, usually the molecule which contains this atom
         * @type {object}
         */
        this.parentMolecule = null
        /** 
         * This atom's value...Is can this be done away with? Are we basically storing the value in the output now?
         * @type {object}
         */
        this.value = null
        /** 
         * A flag to indicate if this atom is currently being draged on the screen.
         * @type {boolean}
         */
        this.isMoving = false
        /** 
         * A flag to indicate if we are hovering over this atom.
         * @type {boolean}
         */
        this.showHover = false
        /** 
         * The X cordinate of this atom now
         * @type {number}
         */

        this.x = 0
        /** 
         * The Y cordinate of this atom now
         * @type {number}
         */
        this.y = 0
        /** 
         * A warning message displayed next to the atom. Put text in here to have a warning automatically show up. Cleared each time the output is regenerated.
         * @type {string}
         */
        this.alertMessage = ''
        /** 
         * A flag to indicate if the atom is currently computing a new output. Turns the molecule blue.
         * @type {boolean}
         */
        this.processing = false
        

        for(var key in values) {
            /** 
             * Assign each of the values in values as this.value
             */
            this[key] = values[key]
        }
        
    }
    
    /**
     * Applies each of the passed values to this as this.x
     * @param {object} values - A list of values to set
     */ 
    setValues(values){
        //Assign the object to have the passed in values
        
        for(var key in values) {
            this[key] = values[key]
        }
        
        if (typeof this.ioValues !== 'undefined') {
            this.ioValues.forEach(ioValue => { //for each saved value
                this.inputs.forEach(io => {  //Find the matching IO and set it to be the saved value
                    if(ioValue.name == io.name && io.type == 'input'){
                        io.value = ioValue.ioValue
                    }
                })
            })
        }
    }

   
    /**
     * Draws the atom on the screen
     */ 
    draw() {
        let xInPixels = GlobalVariables.widthToPixels(this.x)
        let yInPixels = GlobalVariables.heightToPixels(this.y)
        let radiusInPixels = GlobalVariables.widthToPixels(this.radius)

        this.inputs.forEach(child => {
            child.draw()       
        })

        GlobalVariables.c.beginPath()
        GlobalVariables.c.font = '10px Work Sans'

        if(this.processing){
            GlobalVariables.c.fillStyle = 'blue'
        }
        else if(this.selected){
            GlobalVariables.c.fillStyle = this.selectedColor
            GlobalVariables.c.strokeStyle = this.selectedColor
            this.color = this.selectedColor
            this.strokeColor = this.defaultColor
        }
        else{
            GlobalVariables.c.fillStyle = this.defaultColor
            GlobalVariables.c.strokeStyle = this.selectedColor
            this.color = this.defaultColor
            this.strokeColor = this.selectedColor
        }
        
        GlobalVariables.c.arc(xInPixels, yInPixels, radiusInPixels, 0, Math.PI * 2, false)
        GlobalVariables.c.textAlign = 'start'
        GlobalVariables.c.fillText(this.name, xInPixels + radiusInPixels, yInPixels - radiusInPixels)
        GlobalVariables.c.fill()
        GlobalVariables.c.strokeStyle = this.strokeColor
        GlobalVariables.c.lineWidth = 1
        GlobalVariables.c.stroke()
        GlobalVariables.c.closePath()
      
        if (this.alertMessage.length > 0){
            this.color = "red"
            if(this.showHover){
                //Draw Alert block  
                GlobalVariables.c.beginPath()
                const padding = 10
                GlobalVariables.c.fillStyle = 'red'
                GlobalVariables.c.rect(
                    xInPixels + radiusInPixels - padding/2, 
                    yInPixels - radiusInPixels + padding/2, 
                    GlobalVariables.c.measureText(this.alertMessage.toUpperCase()).width + padding, 
                    - (parseInt(GlobalVariables.c.font) + padding))
                GlobalVariables.c.fill()
                GlobalVariables.c.strokeStyle = 'black'
                GlobalVariables.c.lineWidth = 1
                GlobalVariables.c.stroke()
                GlobalVariables.c.closePath()

                GlobalVariables.c.beginPath()
                GlobalVariables.c.fillStyle = 'black'
                GlobalVariables.c.fillText(this.alertMessage.toUpperCase(), xInPixels + radiusInPixels, yInPixels - radiusInPixels) 
                GlobalVariables.c.closePath()
            }
        } 
    }
    
    /**
     * Adds a new attachment point to this atom
     * @param {boolean} type - The type of the IO (input or output)
     * @param {string} name - The name of the new attachment point
     * @param {object} target - The attom to attach the new attachment point to. Should we force this to always be this one?
     * @param {string} valueType - Describes the type of value the input is expecting options are number, geometry, array
     * @param {object} defaultValue - The default value to be used when the value is not yet set
     */ 
    addIO(type, name, target, valueType, defaultValue, ready = false, primary = false){
        
        if(target.inputs.find(o => (o.name === name && o.type === type))== undefined){ //Check to make sure there isn't already an IO with the same type and name
            //compute the baseline offset from parent node
            var offset
            if (type == 'input'){
                offset = -1* target.scaledRadius
            }
            else{
                offset = target.scaledRadius
            }
            var newAp = new AttachmentPoint({
                parentMolecule: target,
                defaultOffsetX: offset,
                defaultOffsetY: 0,
                type: type,
                valueType: valueType,
                name: name,
                primary: primary,
                value: defaultValue,
                defaultValue: defaultValue,
                uniqueID: GlobalVariables.generateUniqueID(),
                atomType: 'AttachmentPoint',
                ready: ready
            })
            
            if(type == 'input'){
                target.inputs.push(newAp)
            }else{
                target.output = newAp
            }
        }
    }
    
    /**
     * Removes an attachment point from an atom.
     * @param {boolean} type - The type of the IO (input or output).
     * @param {string} name - The name of the new attachment point.
     * @param {object} target - The attom which the attachment point is attached to. Should this be forced to be this.?
     */ 
    removeIO(type, name, target){
        //Remove the target IO attachment point
        target.inputs.forEach(input => {
            if(input.name == name && input.type == type){
                target.inputs.splice(target.inputs.indexOf(input),1)
                input.deleteSelf()
            }
        })
    }
    
    /**
     * Set an alert to display next to the atom.
     * @param {string} message - The message to display.
     */ 
    setAlert(message){
        this.color = 'orange'
        this.alertMessage = String(message)

    }
    
    /**
     * Clears the alert message attached to this atom.
     */ 
    clearAlert(){
        this.color = this.defaultColor
        this.alertMessage = ''
    }
    
    /**
     * Set the atom's response to a mouse click. This usually means selecting the atom and displaying it's contents in 3D
     * @param {number} x - The X cordinate of the click
     * @param {number} y - The Y cordinate of the click
     * @param {boolean} clickProcessed - A flag to indicate if the click has already been processed
     */ 
    clickDown(x,y, clickProcessed){

        let xInPixels = GlobalVariables.widthToPixels(this.x)
        let yInPixels = GlobalVariables.heightToPixels(this.y)
        let radiusInPixels = GlobalVariables.widthToPixels(this.radius)

        //If none of the inputs processed the click see if the atom should, if not clicked, then deselect
        if(!clickProcessed && GlobalVariables.distBetweenPoints(x, xInPixels, y, yInPixels) < radiusInPixels){
            //console.log("moving")
            this.isMoving = true
            this.selected = true
            this.updateSidebar()
            this.sendToRender()
            clickProcessed = true
        }
        else{
            this.selected = false
        }

        //Returns true if something was done with the click
        this.inputs.forEach(child => {
            if(child.clickDown(x,y, clickProcessed) == true){
                clickProcessed = true
            }
        })
        if(this.output){
            if(this.output.clickDown(x,y, clickProcessed) == true){
                clickProcessed = true
            }
        }
       
        
        return clickProcessed 
    }

    /**
     * Set the atom's response to a mouse double click. By default this isn't to do anything other than mark the double click as handled.
     * @param {number} x - The X cordinate of the click
     * @param {number} y - The Y cordinate of the click
     */ 
    doubleClick(x,y){
        //returns true if something was done with the click
        let xInPixels = GlobalVariables.widthToPixels(this.x)
        let yInPixels = GlobalVariables.heightToPixels(this.y)
        var clickProcessed = false
        
        var distFromClick = GlobalVariables.distBetweenPoints(x, xInPixels, y, yInPixels)
        
        if (distFromClick < xInPixels){
            clickProcessed = true
        }
        
        return clickProcessed 
    }

    /**
     * Set the atom's response to a mouse click up. If the atom is moving this makes it stop moving.
     * @param {number} x - The X cordinate of the click
     * @param {number} y - The Y cordinate of the click
     */ 
    clickUp(x,y){
        this.isMoving = false
        
        this.inputs.forEach(child => {
            child.clickUp(x,y)     
        })
        if(this.output){
            this.output.clickUp(x,y)
        }
    }
    
    /**
     * Set the atom's response to a mouse click and drag. Moves the atom around the screen.
     * @param {number} x - The X cordinate of the click
     * @param {number} y - The Y cordinate of the click
     */ 
    clickMove(x,y){
        let xInPixels = GlobalVariables.widthToPixels(this.x)
        let yInPixels = GlobalVariables.heightToPixels(this.y)
        let radiusInPixels = GlobalVariables.widthToPixels(this.radius)
        if (this.isMoving == true){
            this.x = GlobalVariables.pixelsToWidth(x)
            this.y = GlobalVariables.pixelsToHeight(y)
        }
        
        this.inputs.forEach(child => {
            child.clickMove(x,y)       
        })
        if(this.output){
            this.output.clickMove(x,y)
        }
        
        var distFromClick = GlobalVariables.distBetweenPoints(x, xInPixels, y, yInPixels)
        
        //If we are close to the attachment point move it to it's hover location to make it accessible
        if (distFromClick < radiusInPixels ){
            this.showHover = true
        }  
        else { this.showHover = false}
    }
    
    /**
     * Set the atom's response to a key press. Is used to delete the atom if it is selected.
     * @param {string} key - The key which has been pressed.
     */ 
    keyPress(key){
        //runs whenever a key is pressed
        if (['Delete', 'Backspace'].includes(key)){
            if(this.selected == true && document.getElementsByTagName('BODY')[0] == document.activeElement){
                //If this atom is selected AND the body is active (meaning we are not typing in a text box)
                this.deleteNode()
            }
        }
        
        this.inputs.forEach(child => {
            child.keyPress(key)
        })
    }
    
    /**
     * Updates the side bar to display information about the atom. By default this is just add a title and to let you edit any unconnected inputs.
     */ 
    updateSidebar(){
        //updates the sidebar to display information about this node
        
        var valueList = this.initializeSideBar()
        
        //Add options to set all of the inputs
        this.inputs.forEach(input => {
            if(input.type == 'input' && input.valueType != 'geometry' && input.connectors.length == 0){
                if(input.valueType == 'number'){
                    this.createEditableValueListItem(valueList,input,'value', input.name, true)
                }
                else{
                    this.createEditableValueListItem(valueList,input,'value', input.name, false)
                }
            }
        })
        
        return valueList
    }
    
    /**
     * Initialized the sidebar with a title and create the HTML object.
     */ 
    initializeSideBar(){

        //remove everything in the sideBar now
        let sideBar = document.querySelector('.sideBar')
        while (sideBar.firstChild) {
            sideBar.removeChild(sideBar.firstChild)
        }

        //adds the name of the molecule to sideBar
        var name2 = document.createElement('p')
        name2.textContent = this.name
        sideBar.appendChild(name2)
        name2.setAttribute('class','molecule_title')


        //add the name as of project title 
        if (this.atomType == 'Molecule' ){
            
            let headerBar_title = document.querySelector('#headerBar_title')
            while (headerBar_title.firstChild) {
                headerBar_title.removeChild(headerBar_title.firstChild)
            }
           
            var name1 = document.createElement('p')
            name1.textContent = "- " + GlobalVariables.topLevelMolecule.name
            headerBar_title.appendChild(name1)

        }

        //Create a list element
        var valueList = document.createElement('ul')
        sideBar.appendChild(valueList)
        valueList.setAttribute('class', 'sidebar-list')

        
        return valueList


    }

    /**
     * Delete this atom.
     */ 
    deleteNode(){
        //deletes this node and all of it's inputs
        
        this.inputs.forEach(input => { //disable the inputs before deleting
            input.ready = false
        })
        
        this.inputs.forEach(input => {
            input.deleteSelf()
        })
        if(this.output){
            this.output.deleteSelf()
        }
        
        this.parent.nodesOnTheScreen.splice(this.parent.nodesOnTheScreen.indexOf(this),1) //remove this node from the list
        
        GlobalVariables.currentMolecule.backgroundClick()
    }
    
    /**
     * Runs with each frame to draw the atom.
     */ 
    update() {
        
        this.inputs.forEach(child => {
            child.update()     
        })
        if(this.output){
            this.output.update()
        }
        
        this.draw()
    }
    /**
     * Save JSON string with information about this atom that we want to save and copy. 
     */ 
    copySelected(){
        if (this.selected){
            GlobalVariables.atomToCopy = (JSON.stringify(this.serialize()))
        }
    }

    /**
     * Paste new copied atom into screen. 
     */ 
    pasteSelected(){    
        let newAtom = JSON.parse(GlobalVariables.atomToCopy)
        let newAtomID = GlobalVariables.generateUniqueID()
        newAtom.uniqueID = newAtomID
        this.placeAtom(newAtom, null, GlobalVariables.availableTypes, false)
    }    
    /**
     * Create an object containing the information about this atom that we want to save. 
     */ 
    serialize(){
        
        var ioValues = []
        this.inputs.forEach(io => {
            if (typeof io.getValue() == 'number' || typeof io.getValue() == 'string'){
                var saveIO = {
                    name: io.name,
                    ioValue: io.getValue()
                }
                ioValues.push(saveIO)
            }
        })
        
        var object = {
            atomType: this.atomType,
            name: this.name,
            x: this.x,
            y: this.y,
            uniqueID: this.uniqueID,
            ioValues: ioValues
        }
        
        return object
    }
    
    /**
     * Return any contribution from this atom to the README file
     */ 
    requestReadme(){
        //request any contributions from this atom to the readme
        
        return []
    }
    
    /**
     * Set's the output value and shows the atom output on the 3D view.
     */ 
    updateValue(){
        
    }
    
    /**
     * Displays the atom in 3D and sets the output.
     */ 
    displayAndPropogate(){
        //If this atom is selected, send the updated value to the renderer
        if (this.selected){
            this.sendToRender()
        }
        
        //Set the output nodes with name 'geometry' to be the generated code
        if(this.output){
            this.output.ready = true
            this.output.waitOnComingInformation() //This sends a chain command through the tree to lock all the inputs which are down stream of this one. This prevents...what? What does this do?
            this.output.setValue(this.value)
        }
    }
    
    /**
     * Calls a worker thread to compute the atom's value.
     */ 
    basicThreadValueProcessing(values, key){
        
        //If the inputs are all ready
        var go = true
        this.inputs.forEach(input => {
            if(!input.ready){
                go = false
            }
        })
        if(go){     //Then we update the value
            this.processing = true
            
            this.clearAlert()
            
            const computeValue = async (values, key) => {
                try{
                    return await GlobalVariables.ask({values: values, key: key})
                }
                catch(err){
                    this.setAlert(err)
                }
            }
            
            computeValue(values, key).then(result => {
                
                if (result != -1 ){
                    this.value = result
                    this.displayAndPropogate()
                }else{
                    this.setAlert("Unable to compute")
                }
                this.processing = false
            })
        }
    }
    
    /**
     * This locks all the inputs downstream of this which will need to wait for it to update
     */
    waitOnComingInformation(){
        this.output.waitOnComingInformation()
    }
    
    /**
     * Unlocks the atom by checking to see if it has any upstream components that it should wait for before beginning to process.
     */ 
    beginPropogation(){
        //Runs right after the loading process to unlock attachment points which have no connectors attached
        this.inputs.forEach(input => {
            if(input.connectors.length == 0){
                input.ready = true
            }
        })
        
        //If the inputs are all ready
        var go = true
        this.inputs.forEach(input => {
            if(!input.ready){
                go = false
            }
        })
        if(go){     //Then we update the value
            this.updateValue()
        }
    }
    
    /**
     * Send the value of this atom to the 3D display.
     */ 
    sendToRender(){
        //Send code to JSxCAD to render
        try{
            GlobalVariables.display.writeToDisplay(this.value)
        }
        catch(err){
            this.setAlert(err)    
        }

    }
    
    /**
     * Find the value of an input for with a given name.
     * @param {string} ioName - The name of the target attachment point.
     */ 
    findIOValue(ioName){
        ioName = ioName.split('~').join('')
        var ioValue = null
        
        this.inputs.forEach(child => {
            if(child.name == ioName && child.type == 'input'){
                ioValue = child.getValue()
            }
        })
        
        return ioValue
    }
    
    /**
     * Dump the stored copies of any geometry in this atom to free up ram.
     */ 
    dumpBuffer(){
        this.inputs.forEach(input => {
            input.dumpBuffer()
        })
        if(this.output){
            this.output.dumpBuffer()
        }
        this.value = null
    }
    
    /**
     * Creates an editable HTML item to set the value of an object element. Used in the sidebar.
     * @param {object} list - The HTML object to attach the new item to.
     * @param {object} object - The object with the element we are editing.
     * @param {string} key - The key of the element to edit.
     * @param {string} label - The label to display next to the editable value.
     * @param {boolean} resultShouldBeNumber - A flag to indicate if the input should be converted to a number.
     * @param {object} callBack - Optional. A function to call with the new value when the value changes.
     */ 
    createEditableValueListItem(list,object,key, label, resultShouldBeNumber, callBack){

        var listElement = document.createElement('LI')
        list.appendChild(listElement)
        
        
        //Div which contains the entire element
        var div = document.createElement('div')
        listElement.appendChild(div)
        div.setAttribute('class', 'sidebar-item sidebar-editable-div')
        
        //Left div which displays the label
        var labelDiv = document.createElement('label')
        div.appendChild(labelDiv)
        var labelText = document.createTextNode(label + ':')
        labelDiv.appendChild(labelText)
        labelDiv.setAttribute('class', 'sidebar-subitem label-item')
        
        
        //Right div which is editable and displays the value
        var valueTextDiv = document.createElement('span')
        labelDiv.appendChild(valueTextDiv)
        var valueText = document.createTextNode(object[key])
        valueTextDiv.appendChild(valueText)
        valueTextDiv.setAttribute('contenteditable', 'true')
        valueTextDiv.setAttribute('class', 'editing-item')
        var thisID = label+GlobalVariables.generateUniqueID()
        valueTextDiv.setAttribute('id', thisID)
        
        
        document.getElementById(thisID).addEventListener('focusout', () => {
            var valueInBox = document.getElementById(thisID).textContent
            if(resultShouldBeNumber){
                valueInBox = GlobalVariables.limitedEvaluate(valueInBox)
            }
            
            //If the target is an attachmentPoint then call the setter function
            if(object instanceof AttachmentPoint){
                object.setValue(valueInBox)
            }
            else{
                object[key] = valueInBox
                callBack(valueInBox)
            }
            
        })
        
        //prevent the return key from being used when editing a value
        document.getElementById(thisID).addEventListener('keypress', function(evt) {
            if (evt.which === 13) {
                evt.preventDefault()
                
                document.getElementById(thisID).blur() 
                //shift focus away if someone presses enter
            }
        })

    }
    
    /**
     * Creates an non-editable HTML item to set the value of an object element. Used in the sidebar.
     * @param {object} list - The HTML object to attach the new item to.
     * @param {object} object - The object with the element we are displaying.
     * @param {string} key - The key of the element to display.
     * @param {string} label - The label to display next to the displayed value.
     */ 
    createNonEditableValueListItem(list,object,key, label){
        var listElement = document.createElement('LI')
        list.appendChild(listElement)
        
        
        //Div which contains the entire element
        var div = document.createElement('div')
        listElement.appendChild(div)
        div.setAttribute('class', 'sidebar-item sidebar-editable-div')
        
        //Left div which displays the label
        var labelDiv = document.createElement('div')
        div.appendChild(labelDiv)
        var labelText = document.createTextNode(label + ':')
        labelDiv.appendChild(labelText)
        labelDiv.setAttribute('class', 'sidebar-subitem label-item')
        
        
        //Right div which is editable and displays the value
        var valueTextDiv = document.createElement('div')
        div.appendChild(valueTextDiv)
        var valueText = document.createTextNode(object[key])
        valueTextDiv.appendChild(valueText)
        valueTextDiv.setAttribute('contenteditable', 'false')
        valueTextDiv.setAttribute('class', 'sidebar-subitem noediting-item')
        var thisID = label+GlobalVariables.generateUniqueID()
        valueTextDiv.setAttribute('id', thisID)
        

    }
    
    /**
     * Creates a html representation of the passed text. Used in the sidebar.
     * @param {object} list - The HTML object to attach the new item to.
     * @param {string} texxt - The text used to generate the markdown html.
     */ 
    createMarkdownListItem(list, text){
        
        var converter = new showdown.Converter()
        //var text      = '# hello, markdown!'
        var html      = converter.makeHtml(text)
        
        var markdownTextDiv = document.createElement('div')
        markdownTextDiv.innerHTML = html
        
        //var valueText = document.createTextNode(text)
        //valueTextDiv.appendChild(valueText)
        list.appendChild(markdownTextDiv)       
    }
    
    /**
     * Creates dropdown with multiple options to select. Used in the sidebar.
     * @param {object} list - The HTML object to attach the new item to.
     * @param {object} parent - The parent which has the function to call on the change...this should really be done with a callback function.
     * @param {array} options - A list of options to display in the drop down.
     * @param {number} selectedOption - The zero referenced index of the selected option.
     * @param {string} description - A description of what the dropdown does.
     * @param {object} Callback function
     */ 
    createDropDown(list,parent,options,selectedOption, description, callback){
        var listElement = document.createElement('LI')
        list.appendChild(listElement)
        
        
        //Div which contains the entire element
        var div = document.createElement('div')
        listElement.appendChild(div)
        div.setAttribute('class', 'sidebar-item')
        
        //Left div which displays the label
        var labelDiv = document.createElement('div')
        div.appendChild(labelDiv)
        var labelText = document.createTextNode(description)
        labelDiv.appendChild(labelText)
        labelDiv.setAttribute('class', 'sidebar-subitem')
        
        
        //Right div which is editable and displays the value
        var valueTextDiv = document.createElement('div')
        div.appendChild(valueTextDiv)
        var dropDown = document.createElement('select')
        options.forEach(option => {
            var op = new Option()
            op.value = options.findIndex(thisOption => thisOption === option)
            op.text = option
            dropDown.options.add(op)
        })
        valueTextDiv.appendChild(dropDown)
        valueTextDiv.setAttribute('class', 'sidebar-subitem')
        
        dropDown.selectedIndex = selectedOption //display the current selection
        
        dropDown.addEventListener(
            'change',
            function() { callback(dropDown.value) },
            false
        )
    }
    
    /**
     * Creates button. Used in the sidebar.
     * @param {object} list - The HTML object to attach the new item to.
     * @param {object} parent - The parent which has the function to call on the change...this should really be done with a callback function.
     * @param {string} buttonText - The text on the button.
     * @param {object} functionToCall - The function to call when the button is pressed.
     */ 
    createButton(list,parent,buttonText,functionToCall){
        var listElement = document.createElement('LI')
        list.appendChild(listElement)
        
        
        //Div which contains the entire element
        var div = document.createElement('div')
        listElement.appendChild(div)
        div.setAttribute('class', 'runSideBarDiv')
        
        
        //Right div which is button
        var valueTextDiv = document.createElement('div')
        div.appendChild(valueTextDiv)
        var button = document.createElement('button')
        var buttonTextNode = document.createTextNode(buttonText)
        button.setAttribute('class', ' browseButton')
        button.setAttribute('id', buttonText + "-button")
        button.appendChild(buttonTextNode)
        valueTextDiv.appendChild(button)
        valueTextDiv.setAttribute('class', 'sidebar-subitem')
        
        button.addEventListener(
            'mousedown',
            function() { functionToCall() } ,
            false
        )
    }
    
    /**
     * Creates button. Used in the sidebar.
     * @param {object} list - The HTML object to attach the new item to.
     * @param {string} buttonText - The text on the button.
     * @param {boolean} - Flag to see if checkbox is checked
     * @param {object} functionToCall - The function to call when the button is pressed.
     */ 
    createCheckbox(sideBar,text,isChecked,callback){
        var gridDiv = document.createElement('div')
        sideBar.appendChild(gridDiv)
        gridDiv.setAttribute('id', 'gridDiv')
        var gridCheck = document.createElement('input')
        gridDiv.appendChild(gridCheck)
        gridCheck.setAttribute('type', 'checkbox')
        gridCheck.setAttribute('id', 'gridCheck')
        
        if (isChecked){
            gridCheck.setAttribute('checked', 'true')
        }
        

        var gridCheckLabel = document.createElement('label')
        gridDiv.appendChild(gridCheckLabel)
        gridCheckLabel.setAttribute('for', 'gridCheck')
        gridCheckLabel.setAttribute('style', 'margin-right:1em;')
        gridCheckLabel.textContent = text

        gridCheck.addEventListener('change', event => {
            callback(event)
        })
    }
}

import Connector from './connector'
import GlobalVariables from '../globalvariables'

/**
 * This class creates a new attachmentPoint which are the input and output blobs on Atoms
 */
export default class AttachmentPoint {
    /**
     * The constructor function.
     * @param {object} values An array of values passed in which will be assigned to the class as this.x
     */ 
    constructor(values){
        
        /** 
         * This atom's default radius (non hover)
         * @type {number}
         */
        this.defaultRadius = 1/60
        /** 
         * A flag to indicate if this attachmet point is currently expanded.
         * @type {boolean}
         */
        this.expandedRadius = false
        /** 
         * This atom's current radius as displayed.
         * @type {number}
         */
        this.radius = 1/60
        
        /** 
         * When the mouse is hovering where should the AP move in X
         * @type {number}
         */
        this.hoverOffsetX = 0
        /** 
         * When the mouse is hovering where should the AP move in Y
         * @type {number}
         */
        this.hoverOffsetY = 0
        /** 
         * A unique identifying number for this attachment point
         * @type {number}
         */
        this.uniqueID = 0
        /** 
         * The default offset position in X referenced to the center of the parent atom.
         * @type {number}
         */
        this.defaultOffsetX = 0
        /** 
         * The default offset position in Y referenced to the center of the parent atom.
         * @type {number}
         */
        this.defaultOffsetY = 0
        /** 
         * The current offset position in X referenced to the center of the parent atom.
         * @type {number}
         */
        this.offsetX = 0
        /** 
         * The current offset position in Y referenced to the center of the parent atom.
         * @type {number}
         */
        this.offsetY = 0
        /** 
         * A flag to determine if the hover text is shown next to the attachment point.
         * @type {boolean}
         */
        this.showHoverText = true
        /** 
         * The attachment point type.
         * @type {string}
         */
        this.atomType = 'AttachmentPoint'
        
        /** 
         * The attachment point value type. Options are number, geometry, array.
         * @type {string}
         */
        this.valueType = 'number'
        /** 
         * The attachment point type. Options are input, output.
         * @type {string}
         */
        this.type = 'output'
        
        /** 
         * This is a flag to indicate if the attachment point is of the primary type.
         * Primary type inputs are of the form geometry.translate(input2, input3, input4) for example
         * This value is useful for importing molecules into other formats.
         * @type {boolean}
         */
        this.primary = false
        
        /** 
         * The attachment point current value.
         * @type {number}
         */
        this.value = 10
        
        /**
         * The default value to be used by the ap when nothing is attached
         * @type {string}
         */
        this.defaultValue = 10
        
        /** 
         * A flag to indicate if the attachment point is currently ready. Used to order initilization when program is loaded.
         * @type {string}
         */
        this.ready = false
        /** 
         * A list of all of the connectors attached to this attachmet point
         * @type {object}
         */
        this.connectors = []
        
        this.offsetX = this.defaultOffsetX
        this.offsetY = this.defaultOffsetY
        
        for(var key in values) {
            /**
             * Assign values in values as this.x
             */
            this[key] = values[key]
        }
        this.clickMove(0,0) //trigger a refresh to get all the current values
    }
    
    /**
     * Draws the attachment point on the screen. Called with each frame.
     */ 
    draw() {
       
        let xInPixels = GlobalVariables.widthToPixels(this.x)
        let yInPixels = GlobalVariables.heightToPixels(this.y)
        let radiusInPixels = GlobalVariables.widthToPixels(this.radius)
        let parentRadiusInPixels = GlobalVariables.widthToPixels(this.parentMolecule.radius)
        let parentXInPixels = GlobalVariables.widthToPixels(this.parentMolecule.x)
        let parentYInPixels = GlobalVariables.heightToPixels(this.parentMolecule.y)

        this.defaultRadius = radiusInPixels
        radiusInPixels = parentRadiusInPixels/2.3

        if (this.expandedRadius){
            radiusInPixels = parentRadiusInPixels/1.8
        }
        if(this.parentMolecule.inputs.length < 2 && this.type == 'input'){
            /**
             * The x cordinate of the attachment point.
             */
            xInPixels = parentXInPixels - parentRadiusInPixels
            /**
             * The y cordinate of the attachment point.
             */
            yInPixels = parentYInPixels
        }    
        else if(this.parentMolecule.inputs.length < 2 && this.type == 'output'){
            xInPixels = parentXInPixels + parentRadiusInPixels
            yInPixels = parentYInPixels
        }                 

        var txt = this.name
        var textWidth = GlobalVariables.c.measureText(txt).width
        GlobalVariables.c.font = '10px Work Sans'

        var bubbleColor = '#C300FF'
        var scaleRadiusDown = radiusInPixels*.7
        var halfRadius = radiusInPixels *.5

        
        if (this.showHoverText){
            if(this.type == 'input'){
 
                GlobalVariables.c.globalCompositeOperation='destination-over'
                GlobalVariables.c.beginPath()

                if (this.name === 'geometry'){
                    GlobalVariables.c.fillStyle = this.parentMolecule.selectedColor   
                }
                else{
                    GlobalVariables.c.fillStyle = bubbleColor
                }
            
                //Draws bubble shape
                GlobalVariables.c.rect(xInPixels - textWidth - radiusInPixels - halfRadius, yInPixels - radiusInPixels, textWidth + radiusInPixels + halfRadius , radiusInPixels*2)   
                GlobalVariables.c.arc(xInPixels - textWidth - radiusInPixels - halfRadius, yInPixels, radiusInPixels, 0, Math.PI * 2, false)

                //Bubble text
                GlobalVariables.c.fill()
                GlobalVariables.c.globalCompositeOperation='source-over'
                GlobalVariables.c.beginPath()
                GlobalVariables.c.fillStyle = this.parentMolecule.defaultColor
                GlobalVariables.c.textAlign = 'end'
                GlobalVariables.c.fillText(this.name, xInPixels - (radiusInPixels + 3), yInPixels+2)
                GlobalVariables.c.fill()
                GlobalVariables.c.closePath()
            }
            else{

                GlobalVariables.c.beginPath()
                    
                if (this.name === 'geometry'){
                    GlobalVariables.c.fillStyle = this.parentMolecule.selectedColor   
                }
                else{
                    GlobalVariables.c.fillStyle = bubbleColor
                }

                GlobalVariables.c.rect(xInPixels, yInPixels - scaleRadiusDown, textWidth + radiusInPixels + halfRadius, scaleRadiusDown*2)
                GlobalVariables.c.arc(xInPixels + textWidth + radiusInPixels + halfRadius, yInPixels, scaleRadiusDown, 0, Math.PI * 2, false)
                GlobalVariables.c.fill()
                GlobalVariables.c.closePath()
                GlobalVariables.c.beginPath()
                GlobalVariables.c.fillStyle = this.parentMolecule.defaultColor
                GlobalVariables.c.textAlign = 'start' 
                GlobalVariables.c.fillText(this.name, (xInPixels + halfRadius) + (radiusInPixels + 3), yInPixels+2)
                GlobalVariables.c.fill()
                GlobalVariables.c.closePath()
            }

        }
 
        GlobalVariables.c.beginPath()
        if(this.ready){
            GlobalVariables.c.fillStyle = this.parentMolecule.color
        }else{
            GlobalVariables.c.fillStyle = '#6ba4ff'
        }
        GlobalVariables.c.strokeStyle = this.parentMolecule.strokeColor
        GlobalVariables.c.lineWidth = 1
        GlobalVariables.c.arc(xInPixels, yInPixels, radiusInPixels, 0, Math.PI * 2, false)
        GlobalVariables.c.fill()
        GlobalVariables.c.stroke()
        GlobalVariables.c.closePath()  

        if (!this.expandedRadius){ 
            if (this.type == 'output'){     
                this.offsetX = this.parentMolecule.radius  
            }
        }
    }
    
    /**
     * Handles mouse click down. If the click is inside the AP it's connectors are selected if it is an input.
     * @param {number} x - The x cordinate of the click
     * @param {number} y - The y cordinate of the click
     * @param {boolean} clickProcessed - Has the click already been handled
     */ 
    clickDown(x,y, clickProcessed){

        let xInPixels = GlobalVariables.widthToPixels(this.x)
        let yInPixels = GlobalVariables.heightToPixels(this.y)

        if(GlobalVariables.distBetweenPoints (xInPixels, x, yInPixels, y) < this.defaultRadius && !clickProcessed){
            if(this.type == 'output'){                  //begin to extend a connector from this if it is an output
                var connector = new Connector({
                    parentMolecule: this.parentMolecule, 
                    attachmentPoint1: this,
                    atomType: 'Connector',
                    isMoving: true
                })
                this.connectors.push(connector)
            }
            
            if(this.type == 'input'){ //connectors can only be selected by clicking on an input
                this.connectors.forEach(connector => {     //select any connectors attached to this node
                    connector.selected = true
                })
            }
            
            return true //indicate that the click was handled by this object
        }
        else{
            if(this.type == 'input'){ //connectors can only be selected by clicking on an input
                this.connectors.forEach(connector => {      //unselect any connectors attached to this node
                    connector.selected = false
                })
            }
            return false //indicate that the click was not handled by this object
        }
    }

    /**
     * Handles mouse click up. If the click is inside the AP and a connector is currently extending, then a connection is made
     * @param {number} x - The x cordinate of the click
     * @param {number} y - The y cordinate of the click
     */ 
    clickUp(x,y){
        this.connectors.forEach(connector => {
            connector.clickUp(x, y)       
        })
    }
    
    /**
     * Handles mouse click and move to expand the AP. Could this be done with a call to expand out?
     * @param {number} x - The x cordinate of the click
     * @param {number} y - The y cordinate of the click
     */ 
    clickMove(x,y){

        let xInPixels = GlobalVariables.widthToPixels(this.x)
        let yInPixels = GlobalVariables.heightToPixels(this.y)
        let radiusInPixels = GlobalVariables.widthToPixels(this.radius)
        
        let parentXInPixels = GlobalVariables.widthToPixels(this.parentMolecule.x)
        let parentYInPixels = GlobalVariables.heightToPixels(this.parentMolecule.y)
        let parentRadiusInPixels = GlobalVariables.widthToPixels(this.parentMolecule.radius)
        //expand if touched by mouse
        var distFromCursorParent = Math.abs(GlobalVariables.distBetweenPoints (parentXInPixels -parentRadiusInPixels, x, parentYInPixels, y)) 
        
        //If we are close to the attachment point move it to it's hover location to make it accessible
        if (distFromCursorParent < parentRadiusInPixels*3){

            if (this.type == 'input'){
                this.expandOut(distFromCursorParent)
                
            }
            this.showHoverText = true
            if (GlobalVariables.distBetweenPoints(xInPixels, x, yInPixels, y) < radiusInPixels/1.2 ){
                this.expandedRadius = true    
            }  
            else{
                this.expandedRadius = false      
            }
        }
        else{
            this.reset()
        }
        
        this.connectors.forEach(connector => {
            connector.clickMove(x, y)       
        })
    }
    
    /**
     * I'm not sure what this does. Can it be deleted?
     */ 
    reset(){

        if (this.type == 'input'){
            this.offsetX = -1* this.parentMolecule.radius
            this.offsetY = this.defaultOffsetY
        }
        this.showHoverText = false
    }
    
    /**
     * Handles mouse click down. If the click is inside the AP it's connectors are selected if it is an input.
     * @param {number} cursorDistance - The distance the cursor is from the attachment point.
     */ 
    expandOut(cursorDistance){

        let radiusInPixels = GlobalVariables.widthToPixels(this.radius)

        const inputList = this.parentMolecule.inputs.filter(input => input.type == 'input')
        const attachmentPointNumber = inputList.indexOf(this) 
        const anglePerIO = (Math.PI) / (inputList.length + 1)
        // angle correction so that it centers menu adjusting to however many attachment points there are 
        const angleCorrection = -Math.PI/2 - anglePerIO
        this.hoverOffsetY = 12 * this.parentMolecule.radius * (Math.sin((attachmentPointNumber * anglePerIO) - angleCorrection))
        this.hoverOffsetX = 4 * this.parentMolecule.radius * (Math.cos((attachmentPointNumber * anglePerIO) - angleCorrection))
        cursorDistance = Math.max( cursorDistance, radiusInPixels*2) //maxes cursor distance so we can hover over each attachment without expansion movement
        //this.offset uses radius in pixels before translating to pixels because that's also the value that limits cursor distance
        this.offsetX = GlobalVariables.widthToPixels(radiusInPixels * 1.2 * this.hoverOffsetX * this.parentMolecule.radius * GlobalVariables.pixelsToWidth((radiusInPixels*3)/cursorDistance))  
        this.offsetY = GlobalVariables.heightToPixels( radiusInPixels* 2.1 * this.hoverOffsetY * this.parentMolecule.radius* GlobalVariables.pixelsToHeight((radiusInPixels*3)/cursorDistance))
    

    }
    
    /**
     * Just passes a key press to the attached connectors. No impact on the connector.
     * @param {string} key - The key which was pressed
     */ 
    keyPress(key){
        this.connectors.forEach(connector => {
            connector.keyPress(key)       
        })
    }
    
    /**
     * Delete any connectors attached to this ap
     */ 
    deleteSelf(){
        //remove any connectors which were attached to this attachment point
        var connectorsList = this.connectors //Make a copy of the list so that we can delete elements without having issues with forEach as we remove things from the list
        connectorsList.forEach( connector => {
            connector.deleteSelf()
        })
    }
    
    /**
     * Delete a target connector which is passed in. The default option is to delete all of the connectors.
     */ 
    deleteConnector(connector = "all"){
        const connectorIndex = this.connectors.indexOf(connector)
        if(connectorIndex != -1){
            this.connectors.splice(connectorIndex,1) //Remove the target connector
        }
        else{
            this.connectors = [] //Remove all of the connectors
        }
    }
    
    /**
     * Can be called to see if the target cordinates are within this ap. Returns true/false.
     * @param {number} x - The x cordinate of the target
     * @param {number} y - The y cordinate of the target
     */ 
    wasConnectionMade(x,y){

        let xInPixels = GlobalVariables.widthToPixels(this.x)
        let yInPixels = GlobalVariables.heightToPixels(this.y)
        let radiusInPixels = GlobalVariables.widthToPixels(this.radius)

        //this function returns itself if the coordinates passed in are within itself
        if (GlobalVariables.distBetweenPoints(xInPixels, x, yInPixels, y) < radiusInPixels && this.type == 'input'){  //If we have released the mouse here and this is an input...
        
            if(this.connectors.length > 0){ //Don't accept a second connection to an input
                return false
            }
            else{
                return true
            }
        }
        else{
            return false
        }
    }
    
    /**
     * Attaches a new connector to this ap
     * @param {object} connector - The connector to attach
     */ 
    attach(connector){
        this.connectors.push(connector)
    }
    
    /**
     * Passes a lock command to the parent molecule, or to the attached connector depending on input/output.
     */ 
    waitOnComingInformation(){
        if(this.type == 'output'){
            this.connectors.forEach(connector => {
                connector.waitOnComingInformation()
            })
        }
        else{
            this.ready = false
            if(this.parentMolecule.output){
                this.parentMolecule.output.waitOnComingInformation()
            }
        }
    }
    
    /**
     * Restores the ap to it's default value.
     */ 
    setDefault(){
        this.setValue(this.defaultValue)
    }
    
    /**
     * Updates the default value for the ap.
     */ 
    updateDefault(newDefault){
        var oldDefault = this.defaultValue
        this.defaultValue = newDefault
        
        if(this.connectors.length == 0 && this.value == oldDefault){    //Update the value to be the default if there is nothing attached
            this.value = this.defaultValue
        }
    }
    
    /**
     * Reads and returns the current value of the ap.
     */ 
    getValue(){
        return this.value
    }
    
    /**
     * Sets the current value of the ap.
     */ 
    setValue(newValue){
        this.value = newValue
        this.ready = true
        //propagate the change to linked elements if this is an output
        if (this.type == 'output'){
            this.connectors.forEach(connector => {     //select any connectors attached to this node
                connector.propogate()
            })
        }
        //if this is an input
        else{   //update the code block to reflect the new values
            this.parentMolecule.updateValue()
        }
    }
    
    /**
     * Clears any references to geometry this ap is holding onto to free up ram.
     */
    dumpBuffer(){
        this.value = null
    }
    
    /**
     * Computes the curent position and then draws the ap on the screen.
     */ 
    update() { 
        this.x = this.parentMolecule.x + this.offsetX
        this.y = this.parentMolecule.y + this.offsetY
        this.draw()
       
        this.connectors.forEach(connector => {  //update any connectors attached to this node
            connector.update()       
        })
    }
}

import GlobalVariables from '../globalvariables'

/**
 * The connector class defines how an output can be connected to an input. It appears on the screen as a black line extending from an output to an input.
 */
export default class Connector {
    
    /**
     * The constructor function.
     * @param {object} values An array of values passed in which will be assigned to the class as this.x
     */ 
    constructor(values){
        
        /** 
         * True if the connector is currently being created and is in the process of extending
         * @type {boolean}
         */
        this.isMoving = false
        /** 
         * The connectors current color
         * @type {string}
         */
        this.color = 'black'
        /** 
         * The type of this connector
         * @type {string}
         */
        this.atomType = 'Connector'
        /** 
         * True if this connector has been selected
         * @type {boolean}
         */
        this.selected = false
        /** 
         * The first attachment point this connector is connected to (an ouput)
         * @type {object}
         */
        this.attachmentPoint1 = null
        /** 
         * The second attachment point this connector is connected to (an input)
         * @type {object}
         */
        this.attachmentPoint2 = null
        
        for(var key in values) {
            /**
             * Assign each of the values in values as this.key
             */
            this[key] = values[key]
        }
        
        /** 
         * The starting X cordinate for the connector. Should really be referenced to attachmentPoint1.
         * @type {number}
         */
        this.startX = this.attachmentPoint1.parentMolecule.outputX
        /** 
         * The starting Y cordinate for the connector. Should really be referenced to attachmentPoint1.
         * @type {number}
         */
        this.startY = this.attachmentPoint1.parentMolecule.y
    }
    
    /**
     * Draw the connector as a bezier curve on the screen
     */ 
    draw(){
        let startXInPixels = GlobalVariables.widthToPixels(this.startX)
        let startYInPixels = GlobalVariables.heightToPixels(this.startY)
        let endXInPixels = GlobalVariables.widthToPixels(this.endX)
        let endYInPixels = GlobalVariables.heightToPixels(this.endY)

        GlobalVariables.c.beginPath()
        GlobalVariables.c.fillStyle = this.color
        GlobalVariables.c.strokeStyle = this.color
        GlobalVariables.c.globalCompositeOperation = 'destination-over' //draw under other elements;
        if(this.selected){
            GlobalVariables.c.lineWidth = 3
        }
        else{
            GlobalVariables.c.lineWidth = 1
        }
        GlobalVariables.c.moveTo(startXInPixels, startYInPixels)
        GlobalVariables.c.bezierCurveTo(startXInPixels + 100, startYInPixels, endXInPixels - 100, endYInPixels, endXInPixels, endYInPixels)
        GlobalVariables.c.stroke()
        GlobalVariables.c.globalCompositeOperation = 'source-over' //switch back to drawing on top
    }
    
    /**
     * clickUp checks to see if the mouse button has been released over an input attachment point. If it has then the connector is created there. If not, then the connector is deleted.
     * @param {number} x - The x cordinate of the click
     * @param {number} y - The y cordinate of the click
     */ 
    clickUp(x,y){
        if(this.isMoving){  //we only want to attach the connector which is currently moving
            var attachmentMade = false
            GlobalVariables.currentMolecule.nodesOnTheScreen.forEach(molecule => {                      //For every molecule on the screen  
                molecule.inputs.forEach(attachmentPoint => {                                    //For each of their attachment points
                    if(attachmentPoint.wasConnectionMade(x,y)){
                        attachmentMade = true
                        this.attachmentPoint2 = attachmentPoint
                        attachmentPoint.attach(this)
                        this.propogate()
                    }
                })
            })
            if (!attachmentMade){
                this.deleteSelf()
            }
            this.isMoving = false
        }
    }
    
    /**
     * clickMove runs while the connector is being created. As long as the mouse is pressed down, the end of the connector stays attached to the mouse.
     * @param {number} x - The x cordinate of the click
     * @param {number} y - The y cordinate of the click
     */ 
    clickMove(x,y){
        if (this.isMoving == true){
            /**
             * The s cordinate of the end of the connector.
             */
            this.endX = GlobalVariables.pixelsToWidth(x)
            /**
             * The y cordinate of the end of the connector.
             */
            this.endY = GlobalVariables.pixelsToHeight(y)
        }
    }
    
    /**
     * Called when any key is pressed. If the key is delete or backspace and the connector is selected then the connector is deleted.
     * @param {string} key - The key which was pressed
     */ 
    keyPress(key){
        if(this.selected){
            if (['Delete', 'Backspace'].includes(key)){
                this.deleteSelf()
            }
        }
    }
    
    /**
     * Deletes the connector by calling its attachmentPoints to tell them to delete their references to this connector.
     */ 
    deleteSelf(){
        //Remove this connector from the output it is attached to
        this.attachmentPoint1.deleteConnector(this) 
        
        //Free up the input to which this was attached
        this.attachmentPoint2.deleteConnector(this)
        this.attachmentPoint2.setDefault()
    }
    
    /**
     * Generates an object used to save the connector.
     */ 
    serialize(){
        if ( this.attachmentPoint2 != null){
            var object = {
                ap1Name: this.attachmentPoint1.name,
                ap2Name: this.attachmentPoint2.name,
                ap2Primary: this.attachmentPoint2.primary,
                ap1ID: this.attachmentPoint1.parentMolecule.uniqueID,
                ap2ID: this.attachmentPoint2.parentMolecule.uniqueID
            }
            return object
        }
    }
    
    /**
     * Passes a lock call to the connected input.
     */ 
    waitOnComingInformation(){
        this.attachmentPoint2.waitOnComingInformation()
    }
    
    /**
     * Pass the value of the attached output to the attached input
     */ 
    propogate(){
        //takes the input and passes it to the output
        if(this.attachmentPoint1.ready){
            this.attachmentPoint2.setValue(this.attachmentPoint1.getValue())
        }
    }
    
    /**
     * Commputes the connectors position and draw it to the screen.
     */ 
    update() {
        this.startX = this.attachmentPoint1.x
        this.startY = this.attachmentPoint1.y
        if (this.attachmentPoint2){  //check to see if the attachment point is defined
            this.endX = this.attachmentPoint2.x
            this.endY = this.attachmentPoint2.y
        }
        this.draw()
    }

}

/**
 * Returns a randome integer within a range.
 * @param {number} min - The minimum possible value.
 * @param {number} max - The maximum possible value.
 */ 
function randomIntFromRange(min, max) {
    return Math.floor(Math.random() * (max - min + 1) + min)
}

/**
 * Selects a random color from an array. Where is this used? Can this be deleted?
 * @param {array} colors - An array of possible colors.
 */ 
function randomColor(colors) {
    return colors[Math.floor(Math.random() * colors.length)]
}

/**
 * Compute the distance between two points on a plane.
 * @param {number} x1 - The x cordinate of the first point.
 * @param {number} y1 - The y cordinate of the first point.
 * @param {number} x2 - The x cordinate of the second point.
 * @param {number} y2 - The y cordinate of the second point.
 */ 
function distance(x1, y1, x2, y2) {
    const xDist = x2 - x1
    const yDist = y2 - y1

    return Math.sqrt(Math.pow(xDist, 2) + Math.pow(yDist, 2))
}

module.exports = { randomIntFromRange, randomColor, distance }
